import './leftSide.css'
import { NavLink } from "react-router-dom";
import { useState} from 'react';

// console.log()
const LeftSide = () => {
    const [HomeNav_BackgroundColor, setHomeNav_BackgroundColor] = useState('black');
    const [CashBookNav_BackgroundColor, setCashBookNav_BackgroundColor] = useState('black');
    const [EnventoryNav_BackgroundColor, setEnventoryNav_BackgroundColor] = useState('black');
    const [KeepSafeNav_BackgroundColor, setKeepSafeNav_BackgroundColor] = useState('black');

    const [isHover, setIsHover] = useState(false);

    const handleMouseEnter = () => {
        setIsHover(true);
    };

    const handleMouseLeave = () => {
        setIsHover(false);
    };

    return (
        <span id='leftSpan' className='desktop'>
            <div id='leftSpan_Overlay'>
                <NavLink style={
                    ({ isActive }) => {
                        isActive ? setHomeNav_BackgroundColor("var(--color5) !important") : setHomeNav_BackgroundColor("var(--color1)")
                        return {
                            color: isActive ? "var(--color1)" : "var(--color5) !important",
                        };
                    }} 
                    className="NavLink" to="/home"
                >
                    <div className='leftNav_selected home' style={{backgroundColor:HomeNav_BackgroundColor}} onMouseOver={hoverIN()} onMouseOut={hoverOut()}>
                        <div id='leftNav_home'></div>
                        <p>Home</p>
                    </div>
                </NavLink>

                <NavLink className="NavLink" to="/ledger">
                    <div className='leftNav_selected cashbook'>
                        <div id='leftNav_cashbook'></div>
                        <p>Cashbook</p>
                    </div>
                </NavLink>

                <NavLink className="NavLink" to="/enventory">
                    <div className='leftNav_selected enventory'>
                        <div id='leftNav_enventory'></div>
                        <p>Enventory</p>
                    </div>
                </NavLink>

                <NavLink className="NavLink" to="/keepSafe">
                    <div className='leftNav_selected keepsafe'>
                        <div id='leftNav_keepsafe'></div>
                        <p>Keep safe</p>
                    </div>
                </NavLink>
            </div>
        </span>
    );
}

export default LeftSide;
