import React, {useState, useEffect, useMemo} from 'react'
import { useSelector, useDispatch } from 'react-redux'
import increase_CurrentNum from './slices/currentNum_Slice'
import increase_Data_Id from './slices/data_Id_Slice'
import data from "../questions.json"  

function Answer() {
    const data_Id = useSelector((state) => state.data_Id.value)
    const numOfQuestions = useSelector((state) => state.numOfQuestions.value)
    const currentNum = useSelector((state) => state.currentNum.value)

    const [answers, setAnswers] = React.useState([]);
    const [answerStatus, setAnswerStatus] = React.useState([]);
    const [nextBTN, setnextBTN] = React.useState(true);
    const [answeredCorrect, setAnsweredCorrect] = React.useState(0);
    const [answeredFailed, setAnsweredFailed] = React.useState(0);
    const [answersArr, setAnswersArr] = React.useState([]);

    const dispatch = useDispatch()

    const nextQuestion =()=>{
        if(currentNum<numOfQuestions){
            dispatch(increase_CurrentNum())
            dispatch(increase_Data_Id())
        }
        const answerClass = document.getElementsByClassName("answers")
        for(let i=0; i<answerClass.length; i++){
            answerClass[i].disabled = false
        }
        setnextBTN(true)
        setAnswerStatus("")
    }

    const validateAns =(e)=>{
        if(e.target.innerText===data[data_Id].correct_answer.replace(/%3A%20|%20/g," ").replace(/%27|%3F|%22|%2C/g,"")){
            setAnswerStatus("Correct!")
            setAnsweredCorrect(answeredCorrect+1)
        }else{
            setAnswerStatus("Sorry!")
            setAnsweredFailed(answeredFailed+1)
        }
        const answerClass = document.getElementsByClassName("answers")
        for(let i=0; i<answerClass.length; i++){
            answerClass[i].disabled = true
        }
        setnextBTN(false)
    }


    const j = () => {
        const answersSubArr = [<button className='answers' onClick={validateAns} >{data[dataId].correct_answer.replace(/%3A%20|%20/g," ").replace(/%27|%3F|%22|%2C|%24/g,"")}</button>]
        data[dataId].incorrect_answers.map(  
           (answers, pk, answersObj)=>{
               answersSubArr.push(<button className='answers' onClick={validateAns} >{answers.replace(/%3A%20|%20/g," ").replace(/%27|%3F|%22|%2C|%24/g,"")}</button>)
           }
       )
       setAnswersArr(answersSubArr)
    }

    useMemo(
        ()=>{
            j()
            const shuffleArray = array => {
                for (let i = array.length - 1; i > 0; i--) {
                  const j = Math.floor(Math.random() * (i + 1));
                  const temp = array[i];
                  array[i] = array[j];
                  array[j] = temp;
                }
              }

            shuffleArray(answersArr)
            setAnswers(answersArr)
        }, []
    )

    console.log(answersArr)
    // console.log(answers)


    return (
        <>
            <div id='answers'>
                {answers}
            </div>

            <h2 id='answerStatus'>{answerStatus}</h2>

            <button id='nextQuestion' onClick={nextQuestion} disabled={nextBTN}>next</button>
        </>
    )
}

export default Answer
