import { useState, useEffect, useMemo } from 'react';
import {get_cashStream} from './api/cashStream_CRUD'
import useAccess from "../../auths/accessToken"
import useGlobalState from "../../../Store_&_State/GlobalState"
import {useEffectOnce} from "../../../custom_Hooks/useEffectOnce"
import Ledger from '../Ledger'

function Streams() {
  const {streamList, setStreamList} = useGlobalState([]);
  const {stream, setStream} = useGlobalState([]);
  const [streamHeader, setStreamHeader] = useState([]);
  const [StreamPages, setStreamPages] = useState([]);
  const [Streampage, setStreamPage] = useState(0);
  const {access, setAccess} = useAccess('')

  const streamWidth = 27.5
  const periodWidth = 46
  const flowWidth = streamWidth/2
  const snWidth = periodWidth*(0.1)
  const dateWidth = periodWidth*(0.2)
  const descriptionWidth = periodWidth*(0.7)

  Ledger(stream.length)

    // CashStream data management----------------------------------------------------------------------------------------------------
  const stream_Success = async(data) => {
    // alert("d")
    await setStreamList(data.results);
    const newPages = [];
    if (data.count > 10) {
      for (let i=0; i<Math.ceil(data.count / 10); i++) {
        newPages.push({
          name: (i+1).toString(),
          page: i,
        });
        console.log("page",i);
      }
      if (Streampage > newPages.length-1) {
        await setStreamPage(Streampage-1);
      }
    } else {
      await setStreamPage(0);
    }
    await setStreamPages(newPages);
  };

  const set_Access = (Access_token) => {
    setAccess(Access_token)
  }

  // console.log(access)

  useEffectOnce(
    () => {
      set_Access()
    },[]
  )

  const getStreamData = async()=>{
    await get_cashStream(Streampage, stream_Success, (text)=>{console.log("Error: ", text)}, access, set_Access );
  };

  useEffectOnce(()=>{
  // useEffect(()=>{
    getStreamData();
  }, [access]);

  useEffect(() => {
    setStream(
      streamList.map(function(item){
        return(
          <div className="th stream" coldiv="2" style={{width:`${streamWidth}%`}}>{item.name}</div>
        )
      })
    );

    setStreamHeader(
      streamList.map(function(item){

        return(
          <>
            <div className="th" rowdiv="2" style={{width:`${flowWidth}%`}}>Credit</div>
            <div className="th" rowdiv="2" style={{width:`${flowWidth}%`}}>Debit</div>
          </>
        )
      })
    )
  }, [streamList]);



    return (
      <div id="thead" >
        <div className="tr" id="trFirstRow">
          <div className="th" coldiv="3" id="period" style={{width:`${periodWidth}vw`}}>12/04/22 - 12/04/22</div>
          {stream}             
        </div>

        <div className="tr" id="trSecondRow">
            <div  className="th" id="snHeader" style={{width:`${snWidth}vw`, position: `sticky`, left: `${0}`}}>S/N</div>
            <div  className="th" id="dateHeader" style={{width:`${dateWidth}vw`, position: `sticky`, left: `${snWidth}%`}}>Date</div>
            <div   className="th" id="descriptionHeader" style={{width:`${descriptionWidth}vw`, position: `sticky`, left: `${snWidth+dateWidth}%`}}>Description</div>
            {streamHeader}
        </div>
      </div>

    )
}

export default Streams
