import { useState, useEffect } from 'react';
import React from 'react'
import Modal from 'react-modal';
import './createFlow.css'
import useGlobalState from "../../../Store_&_State/GlobalState"
import useAccess from "../../auths/accessToken"
import {create_cashFlow} from './api/cashFow_CRUD'
import {get_categories, create_categories} from "./api/categories_api"

function CreateFlow(prop) {
    const [description, setDescription] = useState("");
    const [selected_Streamm, setSelected_Streamm] = useState("");
    const [credit, setCredit] = useState("");
    const [debit, setDebit] = useState("");
    const [categoryList, setcategoryList] = useState("");
    const [newCategory, setNewCategory] = useState("");
    const [selected_Category, setSelected_Category] = useState("");
    const [savedTransaction, setSavedTransaction] = useState('');
    const {createFlowModalIsOpen, setCreateFlowIsOpen} = useGlobalState([]);
    const [selectColor, setSelectColor] = useState("gray");
    const [stream, setStream] = useState([]);

    const {access, setAccess} = useAccess('')
    const {streamList, setStreamList} = useGlobalState([]);

    const create_Success = async(data, page) => {
        closeModal()
    };

    const set_Access = (Access_token) => {
        setAccess(Access_token)
    }

    const create_Category = async(e)=>{
        e.preventDefault()
        await create_categories(newCategory, create_Success, access, set_Access);
    };

    // GET category ......................................................
    const getCategories_success = async(data) => {
        // console.log(data)
        // console.log(data.results)

        setcategoryList(
            data.results.map(function(item){
              return(
                <>
                    <div className='categories'>
                        <input type="radio" id={item.name} name="category" value={item.name}/>
                        <label for={item.name}>{item.name}</label>
                    </div><br/>
                </>
              )
            })
        )

    };

    const getCategories_fail = async(failed) => {
        console.log(failed)
    };

    const load_categories = () => {
        get_categories(getCategories_success, getCategories_fail, access, setAccess)

    }

    useEffect(() => {
        load_categories()


    }, []);

    useEffect(() => {
        setStream(
          streamList.map(function(item){
              console.log(item.id)
            return(
                <button required onClick={(e)=>{ e.preventDefault(); setSelected_Streamm(item.id)}}>{item.name}</button>
            )
          })
        );

    }, [streamList]);

    if (createFlowModalIsOpen){
        prop.blur(8)
    }

    function afterOpenModal() {
        // references are now sync'd and can be accessed.
    }

    function closeModal(e) {
        e.preventDefault();
        setCreateFlowIsOpen(false);
        prop.blur(0)
    }

    const create_Data = async(e)=>{
        e.preventDefault()
        await create_cashFlow(description, selected_Streamm, credit, debit, create_Success, access, set_Access);

    };

    useEffect(
        () => {
            if(savedTransaction == ''){
                setSelectColor("gray")
            }else{
                setSelectColor("black")
            }
        },[savedTransaction]
    )

  return (
    <div>
      <Modal
        isOpen={createFlowModalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        contentLabel="Add new Entry"
        className="Modal"
        overlayClassName="Overlay"
      >
        <form id="loginForm" className='column' >
            <div id='form_blur_Overlay'></div>
            <div id='record_Header_Overlay' className='row'>
                <h2>Add Record</h2> 
                <button onClick={closeModal}>X</button>
            </div>

            <div id='descriptionOverlay' className="">
                Description<br/>
                <input className="record_form" autoFocus type="text" id="description" placeholder="e.g. worker salary" onChange={(e)=>{setDescription(e.target.value)}} value={description} /><br/>
            </div>

            <div id='accountOverlay'>
                Account<br/>
                {stream}
            </div><br/>

            <div id='AmountOverlay' className="inputTextOverlay">
                Amount<br/>
                <div className='row'>
                    <div>
                        Credit<input className="record_form" type="credit" id="password" placeholder="0.00" onChange={(e)=>{setCredit(e.target.value)}} value={credit} />
                    </div>
                    <div>
                        Debit<input className="record_form" type="debit" id="password" placeholder="0.00" onChange={(e)=>{setDebit(e.target.value)}} value={debit} />
                    </div>
                </div>
            </div>

            <div id='savedTransactionOverlay' className='row'>
                <label>
                    <select value={savedTransaction} placeholder='' onChange={(e)=>{setSavedTransaction(e.target.value)}} style={{color:selectColor}} >
                        <option value="">Choose from saved transactions</option>
                        <option value="lime">Lime</option>
                        <option value="coconut">Coconut</option>
                        <option value="mango">Mango</option>
                        {/* <option value="grapefruit">coco</option> */}
                    </select>
                </label>
                <div className='column'>
                    <button></button>
                    Save transaction
                </div>
            </div>

            <div id='categoryOverlay' className='column'>
                Category<br/>
                <div className='row'>
                    {categoryList}
                </div>
            </div>

            <div id='addCategoryOverlay'>
                Add category<br/>
                <input className="add_category" type="text" id="category" onChange={(e)=>{setNewCategory(e.target.value)}} value={newCategory} />
                <button onClick={create_Category}>+</button>
            </div>

            <div id="BTN_overlay" >
                <div>
                    <button id="submit"  value="Done" onClick={closeModal}>cancel</button>
                    <button id="submit" type="submit" value="Done" onClick={create_Data}>Done</button>
                </div>
            </div>
        </form>
      </Modal>
    </div>
  )
}

export default CreateFlow
