import userVerification_api from "./api/userVerification_api"
import React, {useEffect, useState, useRef} from 'react'
// import { Link, useNavigate } from "react-router-dom";
import {useEffectOnce} from "../../../custom_Hooks/useEffectOnce"


function UserVerification() {
    const [verificationStatus, setVerificationStatus] = useState(
        <>
            <p>loading....</p>
        </>
    );
    // let navigate = useNavigate();

    const success = (text)=> {
        console.log(text)
        setVerificationStatus(
            <>
                <p>We just sent a link to your Email,</p>
                <p>click on the link to verify your account.</p>
                <div></div>
            </>
        )
    };

    const fail = (text)=> {
        setVerificationStatus(
            <>
                <p>text</p>
            </>
        )
        console.log(text)
    }; 

    useEffectOnce(() => { 
        const code = window.location.href.slice(48)
        userVerification_api(code, success, fail)
    }, []); 

  return (
    <>
        {verificationStatus}
    </> 
  )
}

export default UserVerification
