import userVerification_api from "./api/userVerification_api"
import React, {useEffect, useState} from 'react'
import { Link, useNavigate } from "react-router-dom";

function UserVerification() {
    const [message, setMessage] = useState("");

    const success = async (text)=> {
        console.log("You are logged in!");
        // await localStorage.setItem("refreshToken", text.refresh);

        setAccess(
          {
            access:text.access,
          }
        )
        navigate("/ledger");
    };

    useEffect(() => {
        const code = window.location.href.slice(48)
        console.log(code)

        userVerification_api(code, success, (text)=>{setMessage(text)})
    }, []);

  return (
    <div>UserVerification</div>
  )
}

export default UserVerification
