import React from 'react'
import { useState, useEffect, useMemo } from 'react';
import {get_cashFlow, delete_cashFlow} from './api/cashFow_CRUD'
import useGlobalState from "../../../Store_&_State/GlobalState"
import useAccess from "../../auths/accessToken"
import {useEffectOnce} from "../../../custom_Hooks/useEffectOnce"
import { ScrollSync, ScrollSyncPane } from 'react-scroll-sync';
import Moment from 'react-moment';
import moment from "moment";

function FlowDomManagement(prop) {
  const [flowList, setFlowList] = useState([]);
  const [flow, setFlow] = useState([]);
  const [flowPages, setFlowPages] = useState([]);
  const [flowPage, setFlowPage] = useState(0);
  const [flow_DateSorted, setFlow_DateSorted] = useState([]);
  const [emptyTR, setEmptyTR] = useState([]);
  const [flowId, setFlowId] = useState([]);
  const {access, setAccess} = useAccess('')
  const {streamList, setStreamList} = useGlobalState([]);
  const {stream, setStream} = useGlobalState([]);
  const {createFlowModalIsOpen, setCreateFlowIsOpen} = useGlobalState([]);
  const {reRender, setReRender} = useGlobalState([0]);

  const streamWidth = 14.4
  const periodWidth = 28
  const flowWidth = streamWidth/2
  const snWidth = periodWidth*(0.1)
  const dateWidth = periodWidth*(0.2)
  const descriptionWidth = periodWidth*(0.7)

  const [display, setDisplay] = useState("none");
  const [offsetTop, setoffsetTop] = useState(0);
  const [trHeight, setTrHeight] = useState(0);
  const table_width = (stream.length*streamWidth) + periodWidth

  const set_Access = (Access_token) => {
    setAccess(Access_token)
  }
  useEffectOnce(() => {
      set_Access()
    },[]
  )

  const flow_Success = async(data, page) => {
    await setFlowList(data.results);
    const newPages = [];
    if (data.count > 10) {
      for (let i=0; i<Math.ceil(data.count / 10); i++) {
        newPages.push({
          name: (i+1).toString(),
          page: i,
        });
        // console.log("page",i);
      }
      if (flowPage > newPages.length-1) {
        await setFlowPage(page-1);
      }
    } else {
      await setFlowPage(0);
    }
    await setFlowPages(newPages);

  };

  const flow_fail = (text) => {
    console.log("Error: ", text)
  }

  const getData = async()=>{
    await get_cashFlow(flowPage, flow_Success, flow_fail, access, set_Access);
  };

  useEffect(()=>{
  // useEffect(()=>{
    getData();
  }, [reRender]);

  const deleteFlowData = async()=>{
    await delete_cashFlow(flowId, () =>{getData()}, access, set_Access);
    // getData()
    setDisplay("none")
  };

  // send flow list to the dom

  const mouseInEditDelete = (e) => {
    let target = e.target
    let scrolled_by = document.getElementsByClassName('tbody_class')[0].scrollTop

    if (target.offsetTop===0){
      const sticky_offset = flowList.length-target.parentElement.firstChild.innerText
      const id = sticky_offset
      let sticky_scroll = document.getElementsByClassName('sticky_offset')[id].offsetTop
      setoffsetTop(sticky_scroll-scrolled_by)
    }else{
      setoffsetTop(target.offsetTop - scrolled_by)
    }

    setFlowId(target.id)
    setTrHeight(target.scrolled_by)
    setDisplay("flex")
  }


  const mouseOutEditDelete = () => {
    // alert('mouce out')
    setDisplay("none")
  }


  useEffect(() => {   
    let newList = []
    const trEmpty = []
    const data = Array.from(streamList)
    data.map(
        function(streamItem, id, arr){
          trEmpty.push(
            <>
              <div className="td" style={{width:`${flowWidth}vw`}}></div>
              <div className="td" style={{width:`${flowWidth}vw`}}></div>
            </>
          );

          return (
              flowList.map(
                async function(flowItem, pk, flow_arr){

                  if (flowItem.CashStream_Data.name === arr[id].name){
                  const nullTd = [];
                  const reversNullTd = [];
                  const editDelete = [];

                    for( var i =0; i < id; i++ ){
                      nullTd.push(
                        <>
                          <div className="td" id={flowItem.id} style={{width:`${flowWidth}vw`}}>0</div>
                          <div className="td" id={flowItem.id} style={{width:`${flowWidth}vw`}}>0</div>
                        </>
                      );
                    };

                    for(var i = (arr.length-1); id < i; i-- ){
                      reversNullTd.push(
                        <>
                          <div className="td" id={flowItem.id} style={{width:`${flowWidth}vw`}}>0</div>
                          <div className="td" id={flowItem.id} style={{width:`${flowWidth}vw`}}>0</div>
                        </>
                      );

                    };

                    const date = moment(new Date(flowItem.created_at.slice(0, 10))).format('YYYY-MM-DD')
                    const time = moment(new Date(flowItem.created_at.slice(0, 10))).format('HH:mm')
                    const sortByDateTime = moment(new Date(flowItem.created_at.slice(0, 10))).format('YYYY-MM-DD HH:mm').replace(/-|:|[ \t]/g,"");
                    // console.log(sortByDateTime)
                    console.log(date)
                    console.log(time)
                    newList.push(
                      <>
                        <div  className="tr" key={sortByDateTime} id={flowItem.id} onMouseMove={mouseInEditDelete} onMouseOver={mouseInEditDelete} onMouseOut={mouseOutEditDelete} >
                          <div className="tr sticky_offset" style={{ position: 'sticky', left: `${0}`}} onMouseMove={mouseInEditDelete} onMouseOver={mouseInEditDelete} onMouseOut={mouseOutEditDelete}>
                            <div className="td" id={flowItem.id} style={{width:`${snWidth}vw`}}>{flow_arr.length-pk}</div> 
                            <div className="td" id={flowItem.id} style={{width:`${dateWidth}vw`}}> 
                              <small style={{textAlign:'center'}}>
                                <Moment format="YYYY/MM/DD HH:mm">
                                  {flowItem.created_at}
                                </Moment>
                              </small>
                            </div>
                            <div className="td discriptionTd" id={flowItem.id} style={{width:`${descriptionWidth}vw`}}>{flowItem.description}</div>
                          </div>
                          {/* {moment(new Date(flowItem.created_at.slice(0, 10))).format('DD-MM-YYYY')} */}
                          <div className='tr'>
                            {nullTd}
                            <div className="td" id={flowItem.id} style={{width:`${flowWidth}vw`}}>{flowItem.Debit}</div> 
                            <div className="td" id={flowItem.id} style={{width:`${flowWidth}vw`}}>{flowItem.Credit}</div>
                            {reversNullTd}
                          </div>
                        </div>
                      </>
                    )
                  }
                }
              )
        )
        }
      )
      console.log(newList)
    setFlow( newList );
    setEmptyTR(trEmpty)
  }, [ access,flowList,flowPage]);



  return (
    <ScrollSyncPane>
          <div id="tbody" className='tbody_class'>
            <div style={{ width:`${table_width}vw`}}>
              {flow.sort(function(a, b){return a.key - b.key})}
              <div className="tr" id="addTr" >
                <div  className="tr" style={{position: 'sticky', left: `${0}`}}>
                  <div className="td" style={{width:`${snWidth}vw`}}></div> 
                  <div className="td" style={{width:`${dateWidth}vw`}}></div>
                  <div className="td discriptionTd" style={{width:`${descriptionWidth}vw`}}>
                    <button id='open_CreateFlowModal_btn' onClick={()=>{setCreateFlowIsOpen(true); prop.blur(8)}}>Add</button>
                  </div>
                </div>

                <div className="tr">{emptyTR}</div>
              </div>
            </div>
            <div id="EditDeleteOverlay" onMouseOver={()=>{ setDisplay("flex") }} onMouseOut={mouseOutEditDelete} style={{  display:display, top:offsetTop, height:trHeight}} >
              <button onClick={deleteFlowData} >X</button><br/>
              <button></button>
            </div>
          </div>
</ScrollSyncPane>

  )
}


export default FlowDomManagement
