import './leftSide.css'
import { NavLink } from "react-router-dom";
import { useState} from 'react';

// console.log()
const LeftSide = () => {
    const [HomeNav_Active, setHomeNav_Active] = useState(false);
    const [CashBookNav_Active, setCashBookNav_Active] = useState(false);
    const [EnventoryNav_Active, setEnventoryNav_Active] = useState(false);
    const [KeepSafeNav_Active, setKeepSafeNav_Active] = useState(false);

    const [isHover, setIsHover] = useState(false);

    const handleMouseEnter = () => {
        setIsHover(true);
    };

    const handleMouseLeave = () => {
        setIsHover(false);
    };

    return (
        <span id='leftSpan' className='desktop'>
            <div id='leftSpan_Overlay'>
                <NavLink style={
                    ({ isActive }) => {
                        isActive ? setHomeNav_Active(true) : setHomeNav_Active(false)
                        return {
                            color: isActive ? "var(--color1)" : "var(--color5) !important",
                        };
                    }} 
                    className="NavLink" to="/home"
                    onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}
                >
                    <div className='leftNav_selected home' style={HomeNav_Active?  {backgroundColor:"var(--color5)"} : (isHover? {backgroundColor:"var(--color5)"} : {backgroundColor:"var(--color1)"})} >
                        <div id='leftNav_home'></div>
                        <p style={isHover? { color:"var(--color1)" } : {color:"var(--color2)"}}>Home</p>
                    </div>
                </NavLink>

                <NavLink className="NavLink" to="/ledger">
                    <div className='leftNav_selected cashbook'>
                        <div id='leftNav_cashbook'></div>
                        <p>Cashbook</p>
                    </div>
                </NavLink>

                <NavLink className="NavLink" to="/enventory">
                    <div className='leftNav_selected enventory'>
                        <div id='leftNav_enventory'></div>
                        <p>Enventory</p>
                    </div>
                </NavLink>

                <NavLink className="NavLink" to="/keepSafe">
                    <div className='leftNav_selected keepsafe'>
                        <div id='leftNav_keepsafe'></div>
                        <p>Keep safe</p>
                    </div>
                </NavLink>
            </div>
        </span>
    );
}

export default LeftSide;
