// import userVerification_api from "./api/userVerification_api"
import React, {useMemo, useState} from 'react'
// import { Link, useNavigate } from "react-router-dom";


const userVerification_api = async (code, success, fail,) => {
    const response = await fetch(`${backendURL}/authemail/signup/verify/?code=${code}`);

    const text = await response.text();

    if (response.status === 200) {
      console.log("Verified", JSON.parse(text));
      success(text);
    } else {
      console.log("failed to Verify", text);
      Object.entries(JSON.parse(text)).forEach(([key, value])=>{
        fail(`${key}: ${value}`);
      });
    }
  };

function UserVerification() {
    const [verificationStatus, setVerificationStatus] = useState(
        <>
            failed to verifyx
        </>
    );
    // let navigate = useNavigate();

    const success = (text)=> {
        setVerificationStatus(
            <>
                {text}
            </>
        )
    };

    const fail = (text)=> {
        console.log(text)
    }; 

    useMemo(() => { 
        const code = window.location.href.slice(48)
        userVerification_api(code, success, fail)
    }, []);

    // success()
        // console.log(message)     

  return (
    <h1>{verificationStatus}</h1>
  )
}

export default UserVerification
