import React, {useEffect, useState, useRef} from 'react'
// import { Link, useNavigate } from "react-router-dom";
import {useEffectOnce} from "../../../custom_Hooks/useEffectOnce"
import { NavLink } from "react-router-dom";


function UserVerification() {
    const [verificationStatus, setVerificationStatus] = useState(
        <>
            <p>loading....</p>
        </>
    );
    // let navigate = useNavigate();

    const success = (text)=> {
        console.log(text)
        setVerificationStatus(
            <>
                <h2>Congratulations</h2>
                <p>start recording and have fun</p>
                <div></div>
                <NavLink style={
                    ({ isActive }) => {
                        isActive ? setGraphsNav_Active(true) : setGraphsNav_Active(false)
                        // return {
                        //     color: isActive ? PColor_active : "var(--color5) !important",
                        // };
                    }} 
                    className="NavLink" to="/ledger/NotAvailableYet/Graphs"
                    onMouseEnter={() =>  {setGraphsIsHoverd(true); setGraphsIsHoverdIcons(true)}} onMouseLeave={() =>  {setGraphsIsHoverd(false); setGraphsIsHoverdIcons(false)}}
                ></NavLink>
                <button></button>
            </>
        )
    };

    const fail = (text)=> {
        setVerificationStatus(
            <>
                <p>text</p>
            </>
        )
        console.log(text)
    }; 

    useEffectOnce(() => { 
        const code = window.location.href.slice(48)
        userVerification_api(code, success, fail)
    }, []); 

  return (
    <>
        {verificationStatus}
    </> 
  )
}

export default UserVerification
