import data from "../../questions.json"  
import { createSlice } from '@reduxjs/toolkit'

const initialState = {
    value: data.length,
}

export const numOfQuestions_slice = createSlice({
    name: 'numOfQuestions',
    initialState,
    reducers: {
      increment: (state) => {
        state.value += 1
      },
    },
  })

  // Action creators are generated for each case reducer function
  export const { increment } = currentNum_Slice.actions

  export default currentNum_Slice.reducer
